@import url('https://fonts.googleapis.com/css2?family=Archivo+Narrow&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Heebo&display=swap');

/*  General Adjustments  */
* {
    padding: 0;
    margin: 0;
}
html {
    box-sizing: border-box;
    scroll-behavior: smooth;
}
.hide_me {
    position:absolute;
    top:-10000px;
}
.home, .products, .about, .contact {
    height: 90vh;
    position: absolute;
    width: 100%;
}
.btn {
    font-family: Heebo,Helvetica,Arial,sans-serif;
    border: none;
    background-color: #8867a1;
    color: white;
    padding: 10px;
    margin: 10px 0;
    font-size: 16px;
    cursor: pointer;
}
.btn:hover {
    background-color: #b26ba5;
}
footer {
    width: 100%;
