    background-color: #FFFFFF;
    height: 25px;
    width: 100%;
    overflow: hidden;
    p {
        text-align: center;
    }
}
#home-footer {
    position: absolute;
    left: 0;
    bottom: 0;
}

/*  Site Navigation  */
.mobile-nav {
    display: none;
}
.site-nav li, .site-nav a, .cart a {
